import{collection,addDoc,serverTimestamp}from"firebase/firestore";import{db}from"./firebase-config.js";const colRef=collection(db,"contacts"),form=document.querySelector(".contact-form"),feedback=document.querySelector(".feedback");form.addEventListener("submit",(e=>{e.preventDefault(),addDoc(colRef,{name:form.name.value,email:form.email.value,message:form.message.value,createdAt:serverTimestamp()}).then((()=>{feedback.style.display="block",setTimeout((()=>{feedback.style.display="none",form.name.value="",form.email.value="",form.message.value=""}),3e3)}))}));